package kr.co.goms.module.common.handler;import android.os.Message;/** * CommonHandlerMessage */public class CommonHandlerMessage {    public Object obj;    public Object obj2;    public Object obj3;    public Object obj4;    public Object callbackObj;    private CommonHandlerMessage(Object obj, Object callbackObj) {        this.obj = obj;        this.callbackObj = callbackObj;    }    private CommonHandlerMessage(Object obj1, Object obj2, Object callbackObj) {        this.obj = obj1;        this.obj2 = obj2;        this.callbackObj = callbackObj;    }    private CommonHandlerMessage(Object obj1, Object obj2, Object obj3, Object callbackObj) {        this.obj = obj1;        this.obj2 = obj2;        this.obj3 = obj3;        this.callbackObj = callbackObj;    }    private CommonHandlerMessage(Object obj1, Object obj2, Object obj3, Object obj4, Object callbackObj) {        this.obj = obj1;        this.obj2 = obj2;        this.obj3 = obj3;        this.obj4 = obj4;        this.callbackObj = callbackObj;    }    public static Message obtainMessage(int arg, Object info, Object callback) {        Message msg = new Message();        msg.what = arg;        msg.obj = new CommonHandlerMessage(info, callback);        return msg;    }    public static Message obtainMessage(int what, Object obj1, Object obj2, Object callback) {        Message msg = new Message();        msg.what = what;        msg.obj = new CommonHandlerMessage(obj1, obj2, callback);        return msg;    }    public static Message obtainMessage(int what, Object obj1, Object obj2, Object obj3, Object callback) {        Message msg = new Message();        msg.what = what;        msg.obj = new CommonHandlerMessage(obj1, obj2, obj3, callback);        return msg;    }    public static Message obtainMessage(int what, Object obj1, Object obj2, Object obj3, Object obj4, Object callback) {        Message msg = new Message();        msg.what = what;        msg.obj = new CommonHandlerMessage(obj1, obj2, obj3, obj4, callback);        return msg;    }}